mapper.select:
SELECT u.id, u.user_name, u.email, u.mobile, u.password, u.status, u.create_time, u.sex, u.dept_id, u.last_login_time, u.update_time, u.remark, u.image, d.dept_name, GROUP_CONCAT(r.id) AS roleIds, GROUP_CONCAT(r.role_name) AS roleNames FROM young_user u LEFT JOIN young_dept d ON (u.dept_id = d.id) LEFT JOIN young_user_role ur ON (u.id = ur.user_id) LEFT JOIN young_role r ON r.id = ur.role_id WHERE 1 = 1
AND u.user_name = #{user.username}
AND d.dept_id = #{user.deptId}
AND u.sex = #{user.sex}
AND u.status = #{user.status}
AND u.mobile = #{user.mobile}
AND d.dept_name like CONCAT('%',#{user.deptName},'%')
group by u.user_name,u.id,u.email,u.mobile,u.password, u.status,u.create_time,u.sex,u.dept_id ,u.last_login_time,u.update_time,u.remark,u.image
